// a library for performing overflow-safe math, courtesy of DappHub (https://github.com/dapphub/ds-math)

library SafeMath {
    function add(uint x, uint y) internal pure returns (uint z) {
        require((z = x + y) >= x, 'ds-math-add-overflow');
    }

    function sub(uint x, uint y) internal pure returns (uint z) {
        require((z = x - y) <= x, 'ds-math-sub-underflow');
    }

    function mul(uint x, uint y) internal pure returns (uint z) {
        require(y == 0 || (z = x * y) / y == x, 'ds-math-mul-overflow');
    }
}
","keccak256":"0x27f0ea82f879b3b01387b583e6d9d0ec858dca3b22b0aad173f8fbda06e761e1"},"contracts/libraries/Tick.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.5.0 <0.8.0;

import "./LowGasSafeMath.sol";
import "./SafeCast.sol";

import "./TickMath.sol";
import "./LiquidityMath.sol";

/// @title Tick
/// @notice Contains functions for managing tick processes and relevant calculations
library Tick {
	using LowGasSafeMath for int256;
	using SafeCast for int256;

	// info stored for each initialized individual tick
	struct Info {
		// the total position liquidity that references this tick
		uint128 liquidityGross;
		// amount of net liquidity added (subtracted) when tick is crossed from left to right (right to left),
		int128 liquidityNet;
		// the cumulative tick value on the other side of the tick
		int56 tickCumulativeOutside;
		// the seconds per unit of liquidity on the _other_ side of this tick (relative to the current tick)
		// only has relative meaning, not absolute — the value depends on when the tick is initialized
		uint160 secondsPerLiquidityOutsideX128;
		// the seconds spent on the other side of the tick (relative to the current tick)
		// only has relative meaning, not absolute — the value depends on when the tick is initialized
		uint32 secondsOutside;
		// true iff the tick is initialized, i.e. the value is exactly equivalent to the expression liquidityGross != 0
		// these 8 bits are set to prevent fresh sstores when crossing newly initialized ticks
		bool initialized;
	}

	/// @notice Derives max liquidity per tick from given tick spacing
	/// @dev Executed within the pool constructor
	/// @param tickSpacing The amount of required tick separation, realized in multiples of `tickSpacing`
	///     e.g., a tickSpacing of 3 requires ticks to be initialized every 3rd tick i.e., ..., -6, -3, 0, 3, 6, ...
	/// @return The max liquidity per tick
	function tickSpacingToMaxLiquidityPerTick(
		int24 tickSpacing
	) internal pure returns (uint128) {
		int24 minTick = (TickMath.MIN_TICK / tickSpacing) * tickSpacing;
		int24 maxTick = (TickMath.MAX_TICK / tickSpacing) * tickSpacing;
		uint24 numTicks = uint24((maxTick - minTick) / tickSpacing) + 1;
		return uint128(-1) / numTicks;
	}

	/// @notice Updates a tick and returns true if the tick was flipped from initialized to uninitialized, or vice versa
	/// @param self The mapping containing all tick information for initialized ticks
	/// @param tick The tick that will be updated
	/// @param liquidityDelta A new amount of liquidity to be added (subtracted) when tick is crossed from left to right (right to left)
	/// @param upper true for updating a position's upper tick, or false for updating a position's lower tick
	/// @param maxLiquidity The maximum liquidity allocation for a single tick
	/// @return flipped Whether the tick was flipped from initialized to uninitialized, or vice versa
	function update(
		mapping(int24 => Tick.Info) storage self,
		int24 tick,
		int128 liquidityDelta,
		bool upper,
		uint128 maxLiquidity
	) internal returns (bool flipped) {
		Tick.Info storage info = self[tick];

		uint128 liquidityGrossBefore = info.liquidityGross;
		uint128 liquidityGrossAfter = LiquidityMath.addDelta(
			liquidityGrossBefore,
			liquidityDelta
		);

		require(liquidityGrossAfter <= maxLiquidity, "LO");

		flipped = (liquidityGrossAfter == 0) != (liquidityGrossBefore == 0);

		if (liquidityGrossBefore == 0) {
			// by convention, we assume that all growth before a tick was initialized happened _below_ the tick
			info.initialized = true;
		}

		info.liquidityGross = liquidityGrossAfter;

		// when the lower (upper) tick is crossed left to right (right to left), liquidity must be added (removed)
		info.liquidityNet = upper
			? int256(info.liquidityNet).sub(liquidityDelta).toInt128()
			: int256(info.liquidityNet).add(liquidityDelta).toInt128();
	}

	/// @notice Clears tick data
	/// @param self The mapping containing all initialized tick information for initialized ticks
	/// @param tick The tick that will be cleared
	function clear(
		mapping(int24 => Tick.Info) storage self,
		int24 tick
	) internal {
		delete self[tick];
	}

	/// @notice Transitions to next tick as needed by price movement
	/// @param self The mapping containing all tick information for initialized ticks
	/// @param tick The destination tick of the transition
	/// @return liquidityNet The amount of liquidity added (subtracted) when tick is crossed from left to right (right to left)
	function cross(
		mapping(int24 => Tick.Info) storage self,
		int24 tick
	) internal returns (int128 liquidityNet) {
		Tick.Info storage info = self[tick];
		liquidityNet = info.liquidityNet;
	}
}
","keccak256":"0x2a40b5e37fb62466f9dbad46b99a0ace57e515bd0c9fdbf70f64c660f32b7b9a"},"contracts/libraries/TickBitmap.sol":{"content":"// SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.5.0;

import "./BitMath.sol";

/// @title Packed tick initialized state library
/// @notice Stores a packed mapping of tick index to its initialized state
/// @dev The mapping uses int16 for keys since ticks are represented as int24 and there are 256 (2^8) values per word.
library TickBitmap {
	/// @notice Computes the position in the mapping where the initialized bit for a tick lives
	/// @param tick The tick for which to compute the position
	/// @return wordPos The key in the mapping containing the word in which the bit is stored
	/// @return bitPos The bit position in the word where the flag is stored
	function position(
		int24 tick
	) private pure returns (int16 wordPos, uint8 bitPos) {
		wordPos = int16(tick >> 8);
		bitPos = uint8(tick % 256);
	}

	/// @notice Flips the initialized state for a given tick from false to true, or vice versa
	/// @param self The mapping in which to flip the tick
	/// @param tick The tick to flip
	/// @param tickSpacing The spacing between usable ticks
	function flipTick(
		mapping(int16 => uint256) storage self,
		int24 tick,
		int24 tickSpacing
	) internal {
		require(tick % tickSpacing == 0); // ensure that the tick is spaced
		(int16 wordPos, uint8 bitPos) = position(tick / tickSpacing);
		uint256 mask = 1 << bitPos;
		self[wordPos] ^= mask;
	}

	/// @notice Returns the next initialized tick contained in the same word (or adjacent word) as the tick that is either
	/// to the left (less than or equal to) or right (greater than) of the given tick
	/// @param self The mapping in which to compute the next initialized tick
	/// @param tick The starting tick
	/// @param tickSpacing The spacing between usable ticks
	/// @param lte Whether to search for the next initialized tick to the left (less than or equal to the starting tick)
	/// @return next The next initialized or uninitialized tick up to 256 ticks away from the current tick
	/// @return initialized Whether the next tick is initialized, as the function only searches within up to 256 ticks
	function nextInitializedTickWithinOneWord(
		mapping(int16 => uint256) storage self,
		int24 tick,
		int24 tickSpacing,
		bool lte
	) internal view returns (int24 next, bool initialized) {
		int24 compressed = tick / tickSpacing;
		if (tick < 0 && tick % tickSpacing != 0) compressed--; // round towards negative infinity

		if (lte) {
			(int16 wordPos, uint8 bitPos) = position(compressed);
			// all the 1s at or to the right of the current bitPos
			uint256 mask = (1 << bitPos) - 1 + (1 << bitPos);
			uint256 masked = self[wordPos] & mask;

			// if there are no initialized ticks to the right of or at the current tick, return rightmost in the word
			initialized = masked != 0;
			// overflow/underflow is possible, but prevented externally by limiting both tickSpacing and tick
			next = initialized
				? (compressed -
					int24(bitPos - BitMath.mostSignificantBit(masked))) *
					tickSpacing
				: (compressed - int24(bitPos)) * tickSpacing;
		} else {
			// start from the word of the next tick, since the current tick state doesn't matter
			(int16 wordPos, uint8 bitPos) = position(compressed + 1);
			// all the 1s at or to the left of the bitPos
			uint256 mask = ~((1 << bitPos) - 1);
			uint256 masked = self[wordPos] & mask;

			// if there are no initialized ticks to the left of the current tick, return leftmost in the word
			initialized = masked != 0;
			// overflow/underflow is possible, but prevented externally by limiting both tickSpacing and tick
			next = initialized
				? (compressed +
					1 +
					int24(BitMath.leastSignificantBit(masked) - bitPos)) *
					tickSpacing
				: (compressed + 1 + int24(uint8(-1) - bitPos)) * tickSpacing;
		}
	}
}
","keccak256":"0xfebb3dabce55440e47183e83be1e0708e3201a2913cf1a11c62b4cf2fea8bed1"},"contracts/libraries/TickMath.sol":{"content":"// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.5.0 <0.8.0;

/// @title Math library for computing sqrt prices from ticks and vice versa
/// @notice Computes sqrt price for ticks of size 1.0001, i.e. sqrt(1.0001^tick) as fixed point Q64.96 numbers. Supports
/// prices between 2**-128 and 2**128
library TickMath {
	/// @dev The minimum tick that may be passed to #getSqrtRatioAtTick computed from log base 1.0001 of 2**-128
	int24 internal constant MIN_TICK = -887272;
	/// @dev The maximum tick that may be passed to #getSqrtRatioAtTick computed from log base 1.0001 of 2**128
	int24 internal constant MAX_TICK = -MIN_TICK;

	/// @dev The minimum value that can be returned from #getSqrtRatioAtTick. Equivalent to getSqrtRatioAtTick(MIN_TICK)
	uint160 internal constant MIN_SQRT_RATIO = 4295128739;
	/// @dev The maximum value that can be returned from #getSqrtRatioAtTick. Equivalent to getSqrtRatioAtTick(MAX_TICK)
	uint160 internal constant MAX_SQRT_RATIO =
		1461446703485210103287273052203988822378723970342;

	/// @notice Calculates sqrt(1.0001^tick) * 2^96
	/// @dev Throws if |tick| > max tick
	/// @param tick The input tick for the above formula
	/// @return sqrtPriceX96 A Fixed point Q64.96 number representing the sqrt of the ratio of the two assets (token1/token0)
	/// at the given tick
	function getSqrtRatioAtTick(
		int24 tick
	) internal pure returns (uint160 sqrtPriceX96) {
		uint256 absTick = tick < 0
			? uint256(-int256(tick))
			: uint256(int256(tick));
		require(absTick <= uint256(MAX_TICK), "T");

		uint256 ratio = absTick & 0x1 != 0
			? 0xfffcb933bd6fad37aa2d162d1a594001
			: 0x100000000000000000000000000000000;
		if (absTick & 0x2 != 0)
			ratio = (ratio * 0xfff97272373d413259a46990580e213a) >> 128;
		if (absTick & 0x4 != 0)
			ratio = (ratio * 0xfff2e50f5f656932ef12357cf3c7fdcc) >> 128;
		if (absTick & 0x8 != 0)
			ratio = (ratio * 0xffe5caca7e10e4e61c3624eaa0941cd0) >> 128;
		if (absTick & 0x10 != 0)
			ratio = (ratio * 0xffcb9843d60f6159c9db58835c926644) >> 128;
		if (absTick & 0x20 != 0)
			ratio = (ratio * 0xff973b41fa98c081472e6896dfb254c0) >> 128;
		if (absTick & 0x40 != 0)
			ratio = (ratio * 0xff2ea16466c96a3843ec78b326b52861) >> 128;
		if (absTick & 0x80 != 0)
			ratio = (ratio * 0xfe5dee046a99a2a811c461f1969c3053) >> 128;
		if (absTick & 0x100 != 0)
			ratio = (ratio * 0xfcbe86c7900a88aedcffc83b479aa3a4) >> 128;
		if (absTick & 0x200 != 0)
			ratio = (ratio * 0xf987a7253ac413176f2b074cf7815e54) >> 128;
		if (absTick & 0x400 != 0)
			ratio = (ratio * 0xf3392b0822b70005940c7a398e4b70f3) >> 128;
		if (absTick & 0x800 != 0)
			ratio = (ratio * 0xe7159475a2c29b7443b29c7fa6e889d9) >> 128;
		if (absTick & 0x1000 != 0)
			ratio = (ratio * 0xd097f3bdfd2022b8845ad8f792aa5825) >> 128;
		if (absTick & 0x2000 != 0)
			ratio = (ratio * 0xa9f746462d870fdf8a65dc1f90e061e5) >> 128;
		if (absTick & 0x4000 != 0)
			ratio = (ratio * 0x70d869a156d2a1b890bb3df62baf32f7) >> 128;
		if (absTick & 0x8000 != 0)
			ratio = (ratio * 0x31be135f97d08fd981231505542fcfa6) >> 128;
		if (absTick & 0x10000 != 0)
			ratio = (ratio * 0x9aa508b5b7a84e1c677de54f3e99bc9) >> 128;
		if (absTick & 0x20000 != 0)
			ratio = (ratio * 0x5d6af8dedb81196699c329225ee604) >> 128;
		if (absTick & 0x40000 != 0)
			ratio = (ratio * 0x2216e584f5fa1ea926041bedfe98) >> 128;
		if (absTick & 0x80000 != 0)
			ratio = (ratio * 0x48a170391f7dc42444e8fa2) >> 128;

		if (tick > 0) ratio = uint256(-1) / ratio;

		// this divides by 1<<32 rounding up to go from a Q128.128 to a Q128.96.
		// we then downcast because we know the result always fits within 160 bits due to our tick input constraint
		// we round up in the division so getTickAtSqrtRatio of the output price is always consistent
		sqrtPriceX96 = uint160(
			(ratio >> 32) + (ratio % (1 << 32) == 0 ? 0 : 1)
		);
	}

	/// @notice Calculates the greatest tick value such that getRatioAtTick(tick) <= ratio
	/// @dev Throws in case sqrtPriceX96 < MIN_SQRT_RATIO, as MIN_SQRT_RATIO is the lowest value getRatioAtTick may
	/// ever return.
	/// @param sqrtPriceX96 The sqrt ratio for which to compute the tick as a Q64.96
	/// @return tick The greatest tick for which the ratio is less than or equal to the input ratio
	function getTickAtSqrtRatio(
		uint160 sqrtPriceX96
	) internal pure returns (int24 tick) {
		// second inequality must be < because the price can never reach the price at the max tick
		require(
			sqrtPriceX96 >= MIN_SQRT_RATIO && sqrtPriceX96 < MAX_SQRT_RATIO,
			"R"
		);
		uint256 ratio = uint256(sqrtPriceX96) << 32;

		uint256 r = ratio;
		uint256 msb = 0;

		assembly {
			let f := shl(7, gt(r, 0xFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFFF))
			msb := or(msb, f)
			r := shr(f, r)
		}
		assembly {
			let f := shl(6, gt(r, 0xFFFFFFFFFFFFFFFF))
			msb := or(msb, f)
			r := shr(f, r)
		}
		assembly {
			let f := shl(5, gt(r, 0xFFFFFFFF))
			msb := or(msb, f)
			r := shr(f, r)
		}
		assembly {
			let f := shl(4, gt(r, 0xFFFF))
			msb := or(msb, f)
			r := shr(f, r)
		}
		assembly {
			let f := shl(3, gt(r, 0xFF))
			msb := or(msb, f)
			r := shr(f, r)
		}
		assembly {
			let f := shl(2, gt(r, 0xF))
			msb := or(msb, f)
			r := shr(f, r)
		}
		assembly {
			let f := shl(1, gt(r, 0x3))
			msb := or(msb, f)
			r := shr(f, r)
		}
		assembly {
			let f := gt(r, 0x1)
			msb := or(msb, f)
		}

		if (msb >= 128) r = ratio >> (msb - 127);
		else r = ratio << (127 - msb);

		int256 log_2 = (int256(msb) - 128) << 64;

		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(63, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(62, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(61, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(60, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(59, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(58, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(57, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(56, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(55, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(54, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(53, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(52, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(51, f))
			r := shr(f, r)
		}
		assembly {
			r := shr(127, mul(r, r))
			let f := shr(128, r)
			log_2 := or(log_2, shl(50, f))
		}

		int256 log_sqrt10001 = log_2 * 255738958999603826347141; // 128.128 number

		int24 tickLow = int24(
			(log_sqrt10001 - 3402992956809132418596140100660247210) >> 128
		);
		int24 tickHi = int24(
			(log_sqrt10001 + 291339464771989622907027621153398088495) >> 128
		);

		tick = tickLow == tickHi
			? tickLow
			: getSqrtRatioAtTick(tickHi) <= sqrtPriceX96
				? tickHi
				: tickLow;
	}
}
","keccak256":"0x3492474e95877f999786d7015787a2e2c5a11b0e13c3f297d4312deb17c1f3aa"},"contracts/libraries/UQ112x112.sol":{"content":"pragma solidity =0.6.6;

// a library for handling binary fixed point numbers (https://en.wikipedia.org/wiki/Q_(number_format))

// range: [0, 2**112 - 1]
// resolution: 1 / 2**112

library UQ112x112 {
    uint224 constant Q112 = 2**112;

    // encode a uint112 as a UQ112x112
    function encode(uint112 y) internal pure returns (uint224 z) {
        z = uint224(y) * Q112; // never overflows
    }

    // divide a UQ112x112 by a uint112, returning a UQ112x112
    function uqdiv(uint224 x, uint112 y) internal pure returns (uint224 z) {
        z = x / uint224(y);
    }
}
