contracts/ButterERC20.sol.content:
pragma solidity =0.6.6;

import './interfaces/IButterERC20.sol';
import './libraries/SafeMath.sol';

abstract contract ButterERC20 is IButterERC20 {
    using SafeMath for uint;

    string public constant override name = 'Uniswap V2';
    string public constant override symbol = 'UNI-V2';
    uint8 public constant override decimals = 18;
    uint  public override totalSupply;
    mapping(address => uint) public override balanceOf;
    mapping(address => mapping(address => uint)) public override allowance;

    bytes32 public override DOMAIN_SEPARATOR;
    // keccak256("Permit(address owner,address spender,uint256 value,uint256 nonce,uint256 deadline)");
    bytes32 public constant override PERMIT_TYPEHASH = 0x6e71edae12b1b97f4d1f60370fef10105fa2faae0126114a169c64845d6126c9;
    mapping(address => uint) public override nonces;

    event Approval(address indexed owner, address indexed spender, uint value);
    event Transfer(address indexed from, address indexed to, uint value);

    constructor() public {
        uint chainId;
        assembly {
            chainId := chainid()
        }
        DOMAIN_SEPARATOR = keccak256(
            abi.encode(
                keccak256('EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)'),
                keccak256(bytes(name)),
                keccak256(bytes('1')),
                chainId,
                address(this)
            )
        );
    }

    function _mint(address to, uint value) internal {
        totalSupply = totalSupply.add(value);
        balanceOf[to] = balanceOf[to].add(value);
        emit Transfer(address(0), to, value);
    }

    function _burn(address from, uint value) internal {
        balanceOf[from] = balanceOf[from].sub(value);
        totalSupply = totalSupply.sub(value);
        emit Transfer(from, address(0), value);
    }

    function _approve(address owner, address spender, uint value) private {
        allowance[owner][spender] = value;
        emit Approval(owner, spender, value);
    }

    function _transfer(address from, address to, uint value) private {
        balanceOf[from] = balanceOf[from].sub(value);
        balanceOf[to] = balanceOf[to].add(value);
        emit Transfer(from, to, value);
    }

    function approve(address spender, uint value) external override returns (bool) {
        _approve(msg.sender, spender, value);
        return true;
    }

    function transfer(address to, uint value) external override returns (bool) {
        _transfer(msg.sender, to, value);
        return true;
    }

    function transferFrom(address from, address to, uint value) external override returns (bool) {
        if (allowance[from][msg.sender] != uint(-1)) {
            allowance[from][msg.sender] = allowance[from][msg.sender].sub(value);
        }
        _transfer(from, to, value);
        return true;
    }

    function permit(address owner, address spender, uint value, uint deadline, uint8 v, bytes32 r, bytes32 s) external override {
        require(deadline >= block.timestamp, 'Butter: EXPIRED');
        bytes32 digest = keccak256(
            abi.encodePacked(
                '\x19\x01',
                DOMAIN_SEPARATOR,
                keccak256(abi.encode(PERMIT_TYPEHASH, owner, spender, value, nonces[owner]++, deadline))
            )
        );
        address recoveredAddress = ecrecover(digest, v, r, s);
        require(recoveredAddress != address(0) && recoveredAddress == owner, 'Butter: INVALID_SIGNATURE');
        _approve(owner, spender, value);
    }
}

contracts/ButterFactory.sol.content:
pragma solidity =0.6.6;

import './interfaces/IButterFactory.sol';
import './ButterPair.sol';

contract ButterFactory is IButterFactory {
    address public override feeTo;
    address public override feeToSetter;
    bytes32 public constant INIT_CODE_HASH = keccak256(abi.encodePacked(type(ButterPair).creationCode));

    mapping(address => mapping(address => address)) public override getPair;
    address[] public override allPairs;

    event PairCreated(address indexed token0, address indexed token1, address pair, uint);

    constructor(address _feeToSetter) public {
        feeToSetter = _feeToSetter;
    }

    function allPairsLength() external view override returns (uint) {
        return allPairs.length;
    }

    function createPair(address tokenA, address tokenB) external override returns (address pair) {
        require(tokenA != tokenB, 'Butter: IDENTICAL_ADDRESSES');
        (address token0, address token1) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        require(token0 != address(0), 'Butter: ZERO_ADDRESS');
        require(getPair[token0][token1] == address(0), 'Butter: PAIR_EXISTS'); // single check is sufficient
        bytes memory bytecode = type(ButterPair).creationCode;
        bytes32 salt = keccak256(abi.encodePacked(token0, token1));
        assembly {
            pair := create2(0, add(bytecode, 32), mload(bytecode), salt)
        }
        IButterPair(pair).initialize(token0, token1);
        getPair[token0][token1] = pair;
        getPair[token1][token0] = pair; // populate mapping in the reverse direction
        allPairs.push(pair);
        emit PairCreated(token0, token1, pair, allPairs.length);
    }

    function setFeeTo(address _feeTo) external override {
        require(msg.sender == feeToSetter, 'Butter: FORBIDDEN');
        feeTo = _feeTo;
    }

    function setFeeToSetter(address _feeToSetter) external override {
        require(msg.sender == feeToSetter, 'Butter: FORBIDDEN');
        feeToSetter = _feeToSetter;
    }
}

contracts/ButterPair.sol.content:
pragma solidity =0.6.6;

import "./interfaces/IButterPair.sol";
import "./ButterERC20.sol";
import "./libraries/Math.sol";
import "./libraries/UQ112x112.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IButterFactory.sol";
import "./interfaces/IButterCallee.sol";
import "./interfaces/IButterERC1155.sol";

import "./libraries/LowGasSafeMath.sol";
import "./libraries/SafeCast.sol";
import "./libraries/Tick.sol";
import "./libraries/TickBitmap.sol";

import "./ButterERC1155.sol";

contract ButterPair is IButterPair, ButterERC20 {
	using SafeMath for uint;
	using UQ112x112 for uint224;

	using Tick for mapping(int24 => Tick.Info);
	using TickBitmap for mapping(int16 => uint256);

	uint public constant override MINIMUM_LIQUIDITY = 10 ** 3;
	bytes4 private constant SELECTOR =
		bytes4(keccak256(bytes("transfer(address,uint256)")));

	address public override factory;
	address public override token0;
	address public override token1;

	IButterERC1155 public token0SwapShare;
	IButterERC1155 public token1SwapShare;

	uint112 private reserve0; // uses single storage slot, accessible via getReserves
	uint112 private reserve1; // uses single storage slot, accessible via getReserves
	uint112 private pendingReserve0; // uses single storage slot, accessible via getReserves
	uint112 private pendingReserve1; // uses single storage slot, accessible via getReserves
	uint32 private blockTimestampLast; // uses single storage slot, accessible via getReserves

	uint public override price0CumulativeLast;
	uint public override price1CumulativeLast;
	uint public override kLast; // reserve0 * reserve1, as of immediately after the most recent liquidity event

	struct Slot0 {
		uint160 lastBlock;
		int24 lastSellTick;
		int24 lastBuyTick;
	}

	int24 public immutable tickSpacing = 10; // TODO: set on deployment
	Slot0 public slot0;

	mapping(int24 => Tick.Info) public ticks;
	mapping(int16 => uint256) public tickBitmap;

	uint private unlocked = 1;
	modifier lock() {
		require(unlocked == 1, "Butter: LOCKED");
		unlocked = 0;
		_;
		unlocked = 1;
	}

	function getReserves()
		public
		view
		override
		returns (
			uint112 _reserve0,
			uint112 _reserve1,
			uint112 _pendingReserve0,
			uint112 _pendingReserve1,
			uint32 _blockTimestampLast
		)
	{
		_reserve0 = reserve0;
		_reserve1 = reserve1;
		_pendingReserve0 = pendingReserve0;
		_pendingReserve1 = pendingReserve1;
		_blockTimestampLast = blockTimestampLast;
	}

	function _safeTransfer(address token, address to, uint value) private {
		(bool success, bytes memory data) = token.call(
			abi.encodeWithSelector(SELECTOR, to, value)
		);
		require(
			success && (data.length == 0 || abi.decode(data, (bool))),
			"Butter: TRANSFER_FAILED"
		);
	}

	event Mint(address indexed sender, uint amount0, uint amount1);
	event Burn(
		address indexed sender,
		uint amount0,
		uint amount1,
		address indexed to
	);
	event Swap(
		address indexed sender,
		uint amount0In,
		uint amount1In,
		uint amount0Out,
		uint amount1Out,
		address indexed to
	);
	event SwapIntention(
		address indexed sender,
		uint amount0In,
		uint amount1In,
		address indexed to
	);
	event Sync(uint112 reserve0, uint112 reserve1);

	constructor() public {
		factory = msg.sender;
		// token0SwapShare = new ButterERC1155(address(this), token0);
		// token1SwapShare = new ButterERC1155(address(this), token1);
	}

	// called once by the factory at time of deployment
	function initialize(address _token0, address _token1) external override {
		require(msg.sender == factory, "Butter: FORBIDDEN"); // sufficient check
		token0 = _token0;
		token1 = _token1;
	}

	function _doClearing() private {}

	// update reserves and, on the first call per block, price accumulators
	function _update(
		uint balance0,
		uint balance1,
		uint112 _reserve0,
		uint112 _reserve1,
		uint112 _pendingReserve0,
		uint112 _pendingReserve1
	) private {
		require(
			balance0 <= uint112(-1) && balance1 <= uint112(-1),
			"Butter: OVERFLOW"
		);
		uint32 blockTimestamp = uint32(block.timestamp % 2 ** 32);
		uint32 timeElapsed = blockTimestamp - blockTimestampLast; // overflow is desired
		if (timeElapsed > 0 && _reserve0 != 0 && _reserve1 != 0) {
			// * never overflows, and + overflow is desired
			price0CumulativeLast +=
				uint(UQ112x112.encode(_reserve1).uqdiv(_reserve0)) *
				timeElapsed;
			price1CumulativeLast +=
				uint(UQ112x112.encode(_reserve0).uqdiv(_reserve1)) *
				timeElapsed;
		}
		reserve0 = uint112(balance0);
		reserve1 = uint112(balance1);
		pendingReserve0 = uint112(_pendingReserve0);
		pendingReserve1 = uint112(_pendingReserve1);
		blockTimestampLast = blockTimestamp;
		emit Sync(reserve0, reserve1);
	}

	// this low-level function should be called from a contract which performs important safety checks
	function mint(address to) external override lock returns (uint liquidity) {
		(
			uint112 _reserve0,
			uint112 _reserve1,
			uint112 _pendingReserve0,
			uint112 _pendingReserve1,

		) = getReserves(); // gas savings
		uint balance0 = IERC20(token0).balanceOf(address(this));
		uint balance1 = IERC20(token1).balanceOf(address(this));
		uint amount0 = balance0.sub(_reserve0);
		uint amount1 = balance1.sub(_reserve1);
		uint _totalSupply = totalSupply; // gas savings, must be defined here since totalSupply can update in _mintFee
		if (_totalSupply == 0) {
			liquidity = Math.sqrt(amount0.mul(amount1)).sub(MINIMUM_LIQUIDITY);
			_mint(address(0), MINIMUM_LIQUIDITY); // permanently lock the first MINIMUM_LIQUIDITY tokens
		} else {
			liquidity = Math.min(
				amount0.mul(_totalSupply) / _reserve0,
				amount1.mul(_totalSupply) / _reserve1
			);
		}
		require(liquidity > 0, "Butter: INSUFFICIENT_LIQUIDITY_MINTED");
		_mint(to, liquidity);

		_update(
			balance0,
			balance1,
			_reserve0,
			_reserve1,
			_pendingReserve0,
			_pendingReserve1
		);
		emit Mint(msg.sender, amount0, amount1);
	}

	// this low-level function should be called from a contract which performs important safety checks
	function burn(
		address to
	) external override lock returns (uint amount0, uint amount1) {
		(
			uint112 _reserve0,
			uint112 _reserve1,
			uint112 _pendingReserve0,
			uint112 _pendingReserve1,

		) = getReserves(); // gas savings
		address _token0 = token0; // gas savings
		address _token1 = token1; // gas savings
		uint balance0 = IERC20(_token0).balanceOf(address(this));
		uint balance1 = IERC20(_token1).balanceOf(address(this));
		uint liquidity = balanceOf[address(this)];

		uint _totalSupply = totalSupply; // gas savings, must be defined here since totalSupply can update in _mintFee
		amount0 = liquidity.mul(balance0) / _totalSupply; // using balances ensures pro-rata distribution
		amount1 = liquidity.mul(balance1) / _totalSupply; // using balances ensures pro-rata distribution
		require(
			amount0 > 0 && amount1 > 0,
			"Butter: INSUFFICIENT_LIQUIDITY_BURNED"
		);
		_burn(address(this), liquidity);
		_safeTransfer(_token0, to, amount0);
		_safeTransfer(_token1, to, amount1);
		balance0 = IERC20(_token0).balanceOf(address(this));
		balance1 = IERC20(_token1).balanceOf(address(this));

		_update(
			balance0,
			balance1,
			_reserve0,
			_reserve1,
			_pendingReserve0,
			_pendingReserve1
		);
		emit Burn(msg.sender, amount0, amount1, to);
	}

	// this low-level function should be called from a contract which performs important safety checks
	function swap(
		uint amount0OutLimit,
		uint amount1OutLimit,
		address to,
		bytes calldata data
	) external override lock {
		require(
			amount0OutLimit > 0 || amount1OutLimit > 0,
			"Butter: INSUFFICIENT_OUTPUT_AMOUNT"
		);
		require(
			!(amount0OutLimit > 0 && amount1OutLimit > 0),
			"Butter: INVALID_OUTPUT_AMOUNT"
		);
		// TODO: check amount0OutLimit, amount1OutLimit

		_doClearing();

		(
			uint112 _reserve0,
			uint112 _reserve1,
			uint112 _pendingReserve0,
			uint112 _pendingReserve1,

		) = getReserves(); // gas savings

		uint balance0;
		uint balance1;
		{
			// scope for _token{0,1}, avoids stack too deep errors
			address _token0 = token0;
			address _token1 = token1;
			require(to != _token0 && to != _token1, "Butter: INVALID_TO");
			balance0 = IERC20(_token0).balanceOf(address(this));
			balance1 = IERC20(_token1).balanceOf(address(this));
		}
		uint amount0In = balance0 > _reserve0 + _pendingReserve0
			? balance0 - (_reserve0 + _pendingReserve0)
			: 0;
		uint amount1In = balance1 > _reserve1 + _pendingReserve1
			? balance1 - (_reserve1 + _pendingReserve1)
			: 0;
		require(
			amount0In <= uint112(-1) && amount1In <= uint112(-1),
			"Butter: OVERFLOW"
		);

		_pendingReserve0 = _pendingReserve0 + uint112(amount0In);
		_pendingReserve1 = _pendingReserve1 + uint112(amount1In);
		require(
			amount0In > 0 || amount1In > 0,
			"Butter: INSUFFICIENT_INPUT_AMOUNT"
		);
		require(
			!(amount0In > 0 && amount1In > 0),
			"Butter: INVALID_INPUT_AMOUNT"
		);

		if (amount0In > 0) {
			token0SwapShare.mint(to, amount1OutLimit, amount0In);
		}
		if (amount1In > 0) {
			token1SwapShare.mint(to, amount0OutLimit, amount1In);
		}

		// // Update TICKs
		// flippedLower = ticks.update(
		//         tickLower,
		//         tick,
		//         liquidityDelta,
		//         _feeGrowthGlobal0X128,
		//         _feeGrowthGlobal1X128,
		//         secondsPerLiquidityCumulativeX128,
		//         tickCumulative,
		//         time,
		//         false,
		//         maxLiquidityPerTick
		//     );
		//     flippedUpper = ticks.update(
		//         tickUpper,
		//         tick,
		//         liquidityDelta,
		//         _feeGrowthGlobal0X128,
		//         _feeGrowthGlobal1X128,
		//         secondsPerLiquidityCumulativeX128,
		//         tickCumulative,
		//         time,
		//         true,
		//         maxLiquidityPerTick
		//     );

		//     if (flippedLower) {
		//         tickBitmap.flipTick(tickLower, tickSpacing);
		//     }
		//     if (flippedUpper) {
		//         tickBitmap.flipTick(tickUpper, tickSpacing);
		//     }

		_update(
			balance0,
			balance1,
			_reserve0,
			_reserve1,
			_pendingReserve0,
			_pendingReserve1
		);
		emit SwapIntention(msg.sender, amount0In, amount1In, to);
	}

	// function swap(
	// 	uint amount0Out,
	// 	uint amount1Out,
	// 	address to,
	// 	bytes calldata data
	// ) external override lock {
	// 	require(
	// 		amount0Out > 0 || amount1Out > 0,
	// 		"Butter: INSUFFICIENT_OUTPUT_AMOUNT"
	// 	);
	// 	require(
	// 		!(amount0Out > 0 && amount1Out > 0),
	// 		"Butter: INVALID_OUTPUT_AMOUNT"
	// 	);

	// 	_doClearing();

	// 	(uint112 _reserve0, uint112 _reserve1, ) = getReserves(); // gas savings
	// 	require(
	// 		amount0Out < _reserve0 && amount1Out < _reserve1,
	// 		"Butter: INSUFFICIENT_LIQUIDITY"
	// 	);

	// 	uint balance0;
	// 	uint balance1;
	// 	{
	// 		// scope for _token{0,1}, avoids stack too deep errors
	// 		address _token0 = token0;
	// 		address _token1 = token1;
	// 		require(to != _token0 && to != _token1, "Butter: INVALID_TO");
	// 		if (amount0Out > 0) _safeTransfer(_token0, to, amount0Out); // optimistically transfer tokens
	// 		if (amount1Out > 0) _safeTransfer(_token1, to, amount1Out); // optimistically transfer tokens
	// 		if (data.length > 0)
	// 			IButterCallee(to).uniswapV2Call(
	// 				msg.sender,
	// 				amount0Out,
	// 				amount1Out,
	// 				data
	// 			);
	// 		balance0 = IERC20(_token0).balanceOf(address(this));
	// 		balance1 = IERC20(_token1).balanceOf(address(this));
	// 	}
	// 	uint amount0In = balance0 > _reserve0 - amount0Out
	// 		? balance0 - (_reserve0 - amount0Out)
	// 		: 0;
	// 	uint amount1In = balance1 > _reserve1 - amount1Out
	// 		? balance1 - (_reserve1 - amount1Out)
	// 		: 0;
	// 	require(
	// 		amount0In > 0 || amount1In > 0,
	// 		"Butter: INSUFFICIENT_INPUT_AMOUNT"
	// 	);
	// 	{
	// 		// scope for reserve{0,1}Adjusted, avoids stack too deep errors
	// 		uint balance0Adjusted = balance0.mul(1000).sub(amount0In.mul(3));
	// 		uint balance1Adjusted = balance1.mul(1000).sub(amount1In.mul(3));
	// 		require(
	// 			balance0Adjusted.mul(balance1Adjusted) >=
	// 				uint(_reserve0).mul(_reserve1).mul(1000 ** 2),
	// 			"Butter: K"
	// 		);
	// 	}

	// 	_update(balance0, balance1, _reserve0, _reserve1);
	// 	emit Swap(msg.sender, amount0In, amount1In, amount0Out, amount1Out, to);
	// }

	// force balances to match reserves
	function skim(address to) external override lock {
		address _token0 = token0; // gas savings
		address _token1 = token1; // gas savings
		_safeTransfer(
			_token0,
			to,
			IERC20(_token0).balanceOf(address(this)).sub(reserve0)
		);
		_safeTransfer(
			_token1,
			to,
			IERC20(_token1).balanceOf(address(this)).sub(reserve1)
		);
	}

	// force reserves to match balances
	function sync() external override lock {
		_update(
			IERC20(token0).balanceOf(address(this)),
			IERC20(token1).balanceOf(address(this)),
			reserve0,
			reserve1,
			pendingReserve0,
			pendingReserve1
		);
	}
}

contracts/ButterRouter02.sol.content:
pragma solidity =0.6.6;

import "./interfaces/IButterFactory.sol";
import "@uniswap/lib/contracts/libraries/TransferHelper.sol";

import "./interfaces/IButterRouter02.sol";
import "./libraries/ButterLibrary.sol";
import "./libraries/SafeMath.sol";
import "./interfaces/IERC20.sol";
import "./interfaces/IWETH.sol";

contract ButterRouter02 is IButterRouter02 {
	using SafeMath for uint;

	address public immutable override factory;
	address public immutable override WETH;

	modifier ensure(uint deadline) {
		require(deadline >= block.timestamp, "ButterRouter: EXPIRED");
		_;
	}

	constructor(address _factory, address _WETH) public {
		factory = _factory;
		WETH = _WETH;
	}

	receive() external payable {
		assert(msg.sender == WETH); // only accept ETH via fallback from the WETH contract
	}

	// **** ADD LIQUIDITY ****
	function _addLiquidity(
		address tokenA,
		address tokenB,
		uint amountADesired,
		uint amountBDesired,
		uint amountAMin,
		uint amountBMin
	) internal virtual returns (uint amountA, uint amountB) {
		// create the pair if it doesn't exist yet
		if (IButterFactory(factory).getPair(tokenA, tokenB) == address(0)) {
			IButterFactory(factory).createPair(tokenA, tokenB);
		}
		(uint reserveA, uint reserveB) = ButterLibrary.getReserves(
			factory,
			tokenA,
			tokenB
		);
		if (reserveA == 0 && reserveB == 0) {
			(amountA, amountB) = (amountADesired, amountBDesired);
		} else {
			uint amountBOptimal = ButterLibrary.quote(
				amountADesired,
				reserveA,
				reserveB
			);
			if (amountBOptimal <= amountBDesired) {
				require(
					amountBOptimal >= amountBMin,
					"ButterRouter: INSUFFICIENT_B_AMOUNT"
				);
				(amountA, amountB) = (amountADesired, amountBOptimal);
			} else {
				uint amountAOptimal = ButterLibrary.quote(
					amountBDesired,
					reserveB,
					reserveA
				);
				assert(amountAOptimal <= amountADesired);
				require(
					amountAOptimal >= amountAMin,
					"ButterRouter: INSUFFICIENT_A_AMOUNT"
				);
				(amountA, amountB) = (amountAOptimal, amountBDesired);
			}
		}
	}
	function addLiquidity(
		address tokenA,
		address tokenB,
		uint amountADesired,
		uint amountBDesired,
		uint amountAMin,
		uint amountBMin,
		address to,
		uint deadline
	)
		external
		virtual
		override
		ensure(deadline)
		returns (uint amountA, uint amountB, uint liquidity)
	{
		(amountA, amountB) = _addLiquidity(
			tokenA,
			tokenB,
			amountADesired,
			amountBDesired,
			amountAMin,
			amountBMin
		);
		address pair = ButterLibrary.pairFor(factory, tokenA, tokenB);
		TransferHelper.safeTransferFrom(tokenA, msg.sender, pair, amountA);
		TransferHelper.safeTransferFrom(tokenB, msg.sender, pair, amountB);
		liquidity = IButterPair(pair).mint(to);
	}
	function addLiquidityETH(
		address token,
		uint amountTokenDesired,
		uint amountTokenMin,
		uint amountETHMin,
		address to,
		uint deadline
	)
		external
		payable
		virtual
		override
		ensure(deadline)
		returns (uint amountToken, uint amountETH, uint liquidity)
	{
		(amountToken, amountETH) = _addLiquidity(
			token,
			WETH,
			amountTokenDesired,
			msg.value,
			amountTokenMin,
			amountETHMin
		);
		address pair = ButterLibrary.pairFor(factory, token, WETH);
		TransferHelper.safeTransferFrom(token, msg.sender, pair, amountToken);
		IWETH(WETH).deposit{ value: amountETH }();
		assert(IWETH(WETH).transfer(pair, amountETH));
		liquidity = IButterPair(pair).mint(to);
		// refund dust eth, if any
		if (msg.value > amountETH)
			TransferHelper.safeTransferETH(msg.sender, msg.value - amountETH);
	}

	// **** REMOVE LIQUIDITY ****
	function removeLiquidity(
		address tokenA,
		address tokenB,
		uint liquidity,
		uint amountAMin,
		uint amountBMin,
		address to,
		uint deadline
	)
		public
		virtual
		override
		ensure(deadline)
		returns (uint amountA, uint amountB)
	{
		address pair = ButterLibrary.pairFor(factory, tokenA, tokenB);
		IButterPair(pair).transferFrom(msg.sender, pair, liquidity); // send liquidity to pair
		(uint amount0, uint amount1) = IButterPair(pair).burn(to);
		(address token0, ) = ButterLibrary.sortTokens(tokenA, tokenB);
		(amountA, amountB) = tokenA == token0
			? (amount0, amount1)
			: (amount1, amount0);
		require(amountA >= amountAMin, "ButterRouter: INSUFFICIENT_A_AMOUNT");
		require(amountB >= amountBMin, "ButterRouter: INSUFFICIENT_B_AMOUNT");
	}
	function removeLiquidityETH(
		address token,
		uint liquidity,
		uint amountTokenMin,
		uint amountETHMin,
		address to,
		uint deadline
	)
		public
		virtual
		override
		ensure(deadline)
		returns (uint amountToken, uint amountETH)
	{
		(amountToken, amountETH) = removeLiquidity(
			token,
			WETH,
			liquidity,
			amountTokenMin,
			amountETHMin,
			address(this),
			deadline
		);
		TransferHelper.safeTransfer(token, to, amountToken);
		IWETH(WETH).withdraw(amountETH);
		TransferHelper.safeTransferETH(to, amountETH);
	}
	function removeLiquidityWithPermit(
		address tokenA,
		address tokenB,
		uint liquidity,
		uint amountAMin,
		uint amountBMin,
		address to,
		uint deadline,
		bool approveMax,
		uint8 v,
		bytes32 r,
		bytes32 s
	) external virtual override returns (uint amountA, uint amountB) {
		address pair = ButterLibrary.pairFor(factory, tokenA, tokenB);
		uint value = approveMax ? uint(-1) : liquidity;
		IButterPair(pair).permit(
			msg.sender,
			address(this),
			value,
			deadline,
			v,
			r,
			s
		);
		(amountA, amountB) = removeLiquidity(
			tokenA,
			tokenB,
			liquidity,
			amountAMin,
			amountBMin,
			to,
			deadline
		);
	}
	function removeLiquidityETHWithPermit(
		address token,
		uint liquidity,
		uint amountTokenMin,
		uint amountETHMin,
		address to,
		uint deadline,
		bool approveMax,
		uint8 v,
		bytes32 r,
		bytes32 s
	) external virtual override returns (uint amountToken, uint amountETH) {
		address pair = ButterLibrary.pairFor(factory, token, WETH);
		uint value = approveMax ? uint(-1) : liquidity;
		IButterPair(pair).permit(
			msg.sender,
			address(this),
			value,
			deadline,
			v,
			r,
			s
		);
		(amountToken, amountETH) = removeLiquidityETH(
			token,
			liquidity,
			amountTokenMin,
			amountETHMin,
			to,
			deadline
		);
	}

	// **** REMOVE LIQUIDITY (supporting fee-on-transfer tokens) ****
	function removeLiquidityETHSupportingFeeOnTransferTokens(
		address token,
		uint liquidity,
		uint amountTokenMin,
		uint amountETHMin,
		address to,
		uint deadline
	) public virtual override ensure(deadline) returns (uint amountETH) {
		(, amountETH) = removeLiquidity(
			token,
			WETH,
			liquidity,
			amountTokenMin,
			amountETHMin,
			address(this),
			deadline
		);
		TransferHelper.safeTransfer(
			token,
			to,
			IERC20(token).balanceOf(address(this))
		);
		IWETH(WETH).withdraw(amountETH);
		TransferHelper.safeTransferETH(to, amountETH);
	}
	function removeLiquidityETHWithPermitSupportingFeeOnTransferTokens(
		address token,
		uint liquidity,
		uint amountTokenMin,
		uint amountETHMin,
		address to,
		uint deadline,
		bool approveMax,
		uint8 v,
		bytes32 r,
		bytes32 s
	) external virtual override returns (uint amountETH) {
		address pair = ButterLibrary.pairFor(factory, token, WETH);
		uint value = approveMax ? uint(-1) : liquidity;
		IButterPair(pair).permit(
			msg.sender,
			address(this),
			value,
			deadline,
			v,
			r,
			s
		);
		amountETH = removeLiquidityETHSupportingFeeOnTransferTokens(
			token,
			liquidity,
			amountTokenMin,
			amountETHMin,
			to,
			deadline
		);
	}

	// **** SWAP ****
	// requires the initial amount to have already been sent to the first pair
	function _swap(
		uint[] memory amounts,
		address[] memory path,
		address _to
	) internal virtual {
		for (uint i; i < path.length - 1; i++) {
			(address input, address output) = (path[i], path[i + 1]);
			(address token0, ) = ButterLibrary.sortTokens(input, output);
			uint amountOut = amounts[i + 1];
			(uint amount0Out, uint amount1Out) = input == token0
				? (uint(0), amountOut)
				: (amountOut, uint(0));
			address to = i < path.length - 2
				? ButterLibrary.pairFor(factory, output, path[i + 2])
				: _to;
			IButterPair(ButterLibrary.pairFor(factory, input, output)).swap(
				amount0Out,
				amount1Out,
				to,
				new bytes(0)
			);
		}
	}
	function swapExactTokensForTokens(
		uint amountIn,
		uint amountOutMin,
		address[] calldata path,
		address to,
		uint deadline
	)
		external
		virtual
		override
		ensure(deadline)
		returns (uint[] memory amounts)
	{
		amounts = ButterLibrary.getAmountsOut(factory, amountIn, path);
		require(
			amounts[amounts.length - 1] >= amountOutMin,
			"ButterRouter: INSUFFICIENT_OUTPUT_AMOUNT"
		);
		TransferHelper.safeTransferFrom(
			path[0],
			msg.sender,
			ButterLibrary.pairFor(factory, path[0], path[1]),
			amounts[0]
		);
		_swap(amounts, path, to);
	}
	// function swapTokensForExactTokens(
	//     uint amountOut,
	//     uint amountInMax,
	//     address[] calldata path,
	//     address to,
	//     uint deadline
	// ) external virtual override ensure(deadline) returns (uint[] memory amounts) {
	//     amounts = ButterLibrary.getAmountsIn(factory, amountOut, path);
	//     require(amounts[0] <= amountInMax, 'ButterRouter: EXCESSIVE_INPUT_AMOUNT');
	//     TransferHelper.safeTransferFrom(
	//         path[0], msg.sender, ButterLibrary.pairFor(factory, path[0], path[1]), amounts[0]
	//     );
	//     _swap(amounts, path, to);
	// }
	function swapExactETHForTokens(
		uint amountOutMin,
		address[] calldata path,
		address to,
		uint deadline
	)
		external
		payable
		virtual
		override
		ensure(deadline)
		returns (uint[] memory amounts)
	{
		require(path[0] == WETH, "ButterRouter: INVALID_PATH");
		amounts = ButterLibrary.getAmountsOut(factory, msg.value, path);
		require(
			amounts[amounts.length - 1] >= amountOutMin,
			"ButterRouter: INSUFFICIENT_OUTPUT_AMOUNT"
		);
		IWETH(WETH).deposit{ value: amounts[0] }();
		assert(
			IWETH(WETH).transfer(
				ButterLibrary.pairFor(factory, path[0], path[1]),
				amounts[0]
			)
		);
		_swap(amounts, path, to);
	}
	// function swapTokensForExactETH(uint amountOut, uint amountInMax, address[] calldata path, address to, uint deadline)
	//     external
	//     virtual
	//     override
	//     ensure(deadline)
	//     returns (uint[] memory amounts)
	// {
	//     require(path[path.length - 1] == WETH, 'ButterRouter: INVALID_PATH');
	//     amounts = ButterLibrary.getAmountsIn(factory, amountOut, path);
	//     require(amounts[0] <= amountInMax, 'ButterRouter: EXCESSIVE_INPUT_AMOUNT');
	//     TransferHelper.safeTransferFrom(
	//         path[0], msg.sender, ButterLibrary.pairFor(factory, path[0], path[1]), amounts[0]
	//     );
	//     _swap(amounts, path, address(this));
	//     IWETH(WETH).withdraw(amounts[amounts.length - 1]);
	//     TransferHelper.safeTransferETH(to, amounts[amounts.length - 1]);
	// }
	function swapExactTokensForETH(
		uint amountIn,
		uint amountOutMin,
		address[] calldata path,
		address to,
		uint deadline
	)
		external
		virtual
		override
		ensure(deadline)
		returns (uint[] memory amounts)
	{
		require(path[path.length - 1] == WETH, "ButterRouter: INVALID_PATH");
		amounts = ButterLibrary.getAmountsOut(factory, amountIn, path);
		require(
			amounts[amounts.length - 1] >= amountOutMin,
			"ButterRouter: INSUFFICIENT_OUTPUT_AMOUNT"
		);
		TransferHelper.safeTransferFrom(
			path[0],
			msg.sender,
			ButterLibrary.pairFor(factory, path[0], path[1]),
			amounts[0]
		);
		_swap(amounts, path, address(this));
		IWETH(WETH).withdraw(amounts[amounts.length - 1]);
		TransferHelper.safeTransferETH(to, amounts[amounts.length - 1]);
	}
	// function swapETHForExactTokens(uint amountOut, address[] calldata path, address to, uint deadline)
	//     external
	//     virtual
	//     override
	//     payable
	//     ensure(deadline)
	//     returns (uint[] memory amounts)
	// {
	//     require(path[0] == WETH, 'ButterRouter: INVALID_PATH');
	//     amounts = ButterLibrary.getAmountsIn(factory, amountOut, path);
	//     require(amounts[0] <= msg.value, 'ButterRouter: EXCESSIVE_INPUT_AMOUNT');
	//     IWETH(WETH).deposit{value: amounts[0]}();
	//     assert(IWETH(WETH).transfer(ButterLibrary.pairFor(factory, path[0], path[1]), amounts[0]));
	//     _swap(amounts, path, to);
	//     // refund dust eth, if any
	//     if (msg.value > amounts[0]) TransferHelper.safeTransferETH(msg.sender, msg.value - amounts[0]);
	// }

	// **** SWAP (supporting fee-on-transfer tokens) ****
	// requires the initial amount to have already been sent to the first pair
	function _swapSupportingFeeOnTransferTokens(
		address[] memory path,
		address _to
	) internal virtual {
		for (uint i; i < path.length - 1; i++) {
			(address input, address output) = (path[i], path[i + 1]);
			(address token0, ) = ButterLibrary.sortTokens(input, output);
			IButterPair pair = IButterPair(
				ButterLibrary.pairFor(factory, input, output)
			);
			uint amountInput;
			uint amountOutput;
			{
				// scope to avoid stack too deep errors
				(uint reserve0, uint reserve1, , , ) = pair.getReserves();
				(uint reserveInput, uint reserveOutput) = input == token0
					? (reserve0, reserve1)
					: (reserve1, reserve0);
				amountInput = IERC20(input).balanceOf(address(pair)).sub(
					reserveInput
				);
				amountOutput = ButterLibrary.getAmountOut(
					amountInput,
					reserveInput,
					reserveOutput
				);
			}
			(uint amount0Out, uint amount1Out) = input == token0
				? (uint(0), amountOutput)
				: (amountOutput, uint(0));
			address to = i < path.length - 2
				? ButterLibrary.pairFor(factory, output, path[i + 2])
				: _to;
			pair.swap(amount0Out, amount1Out, to, new bytes(0));
		}
	}
	// function swapExactTokensForTokensSupportingFeeOnTransferTokens(
	//     uint amountIn,
	//     uint amountOutMin,
	//     address[] calldata path,
	//     address to,
	//     uint deadline
	// ) external virtual override ensure(deadline) {
	//     TransferHelper.safeTransferFrom(
	//         path[0], msg.sender, ButterLibrary.pairFor(factory, path[0], path[1]), amountIn
	//     );
	//     uint balanceBefore = IERC20(path[path.length - 1]).balanceOf(to);
	//     _swapSupportingFeeOnTransferTokens(path, to);
	//     require(
	//         IERC20(path[path.length - 1]).balanceOf(to).sub(balanceBefore) >= amountOutMin,
	//         'ButterRouter: INSUFFICIENT_OUTPUT_AMOUNT'
	//     );
	// }
	// function swapExactETHForTokensSupportingFeeOnTransferTokens(
	//     uint amountOutMin,
	//     address[] calldata path,
	//     address to,
	//     uint deadline
	// )
	//     external
	//     virtual
	//     override
	//     payable
	//     ensure(deadline)
	// {
	//     require(path[0] == WETH, 'ButterRouter: INVALID_PATH');
	//     uint amountIn = msg.value;
	//     IWETH(WETH).deposit{value: amountIn}();
	//     assert(IWETH(WETH).transfer(ButterLibrary.pairFor(factory, path[0], path[1]), amountIn));
	//     uint balanceBefore = IERC20(path[path.length - 1]).balanceOf(to);
	//     _swapSupportingFeeOnTransferTokens(path, to);
	//     require(
	//         IERC20(path[path.length - 1]).balanceOf(to).sub(balanceBefore) >= amountOutMin,
	//         'ButterRouter: INSUFFICIENT_OUTPUT_AMOUNT'
	//     );
	// }
	// function swapExactTokensForETHSupportingFeeOnTransferTokens(
	//     uint amountIn,
	//     uint amountOutMin,
	//     address[] calldata path,
	//     address to,
	//     uint deadline
	// )
	//     external
	//     virtual
	//     override
	//     ensure(deadline)
	// {
	//     require(path[path.length - 1] == WETH, 'ButterRouter: INVALID_PATH');
	//     TransferHelper.safeTransferFrom(
	//         path[0], msg.sender, ButterLibrary.pairFor(factory, path[0], path[1]), amountIn
	//     );
	//     _swapSupportingFeeOnTransferTokens(path, address(this));
	//     uint amountOut = IERC20(WETH).balanceOf(address(this));
	//     require(amountOut >= amountOutMin, 'ButterRouter: INSUFFICIENT_OUTPUT_AMOUNT');
	//     IWETH(WETH).withdraw(amountOut);
	//     TransferHelper.safeTransferETH(to, amountOut);
	// }

	// **** LIBRARY FUNCTIONS ****
	function quote(
		uint amountA,
		uint reserveA,
		uint reserveB
	) public pure virtual override returns (uint amountB) {
		return ButterLibrary.quote(amountA, reserveA, reserveB);
	}

	function getAmountOut(
		uint amountIn,
		uint reserveIn,
		uint reserveOut
	) public pure virtual override returns (uint amountOut) {
		return ButterLibrary.getAmountOut(amountIn, reserveIn, reserveOut);
	}

	function getAmountIn(
		uint amountOut,
		uint reserveIn,
		uint reserveOut
	) public pure virtual override returns (uint amountIn) {
		return ButterLibrary.getAmountIn(amountOut, reserveIn, reserveOut);
	}

	function getAmountsOut(
		uint amountIn,
		address[] memory path
	) public view virtual override returns (uint[] memory amounts) {
		return ButterLibrary.getAmountsOut(factory, amountIn, path);
	}

	function getAmountsIn(
		uint amountOut,
		address[] memory path
	) public view virtual override returns (uint[] memory amounts) {
		return ButterLibrary.getAmountsIn(factory, amountOut, path);
	}
}

contracts/ERC1155.sol.content:
pragma solidity >=0.5.0;

import "./libraries/SafeMath.sol";
import "./libraries/Address.sol";
import "./interfaces/IERC1155.sol";

// A sample implementation of core ERC1155 function.
contract ERC1155 is IERC1155 {
    using SafeMath for uint256;
    using Address for address;

    bytes4 constant internal ERC1155_ACCEPTED = 0xf23a6e61; // bytes4(keccak256("onERC1155Received(address,address,uint256,uint256,bytes)"))
    bytes4 constant internal ERC1155_BATCH_ACCEPTED = 0xbc197c81; // bytes4(keccak256("onERC1155BatchReceived(address,address,uint256[],uint256[],bytes)"))

    // id => (owner => balance)
    mapping (uint256 => mapping(address => uint256)) internal balances;

    // owner => (operator => approved)
    mapping (address => mapping(address => bool)) internal operatorApproval;

/////////////////////////////////////////// ERC165 //////////////////////////////////////////////

    /*
        bytes4(keccak256('supportsInterface(bytes4)'));
    */
    bytes4 constant private INTERFACE_SIGNATURE_ERC165 = 0x01ffc9a7;

    /*
        bytes4(keccak256("safeTransferFrom(address,address,uint256,uint256,bytes)")) ^
        bytes4(keccak256("safeBatchTransferFrom(address,address,uint256[],uint256[],bytes)")) ^
        bytes4(keccak256("balanceOf(address,uint256)")) ^
        bytes4(keccak256("balanceOfBatch(address[],uint256[])")) ^
        bytes4(keccak256("setApprovalForAll(address,bool)")) ^
        bytes4(keccak256("isApprovedForAll(address,address)"));
    */
    bytes4 constant private INTERFACE_SIGNATURE_ERC1155 = 0xd9b67a26;

    function supportsInterface(bytes4 _interfaceId)
    public
    view
    virtual
    override
    returns (bool) {
         if (_interfaceId == INTERFACE_SIGNATURE_ERC165 ||
             _interfaceId == INTERFACE_SIGNATURE_ERC1155) {
            return true;
         }

         return false;
    }

/////////////////////////////////////////// ERC1155 //////////////////////////////////////////////

    /**
        @notice Transfers `_value` amount of an `_id` from the `_from` address to the `_to` address specified (with safety call).
        @dev Caller must be approved to manage the tokens being transferred out of the `_from` account (see "Approval" section of the standard).
        MUST revert if `_to` is the zero address.
        MUST revert if balance of holder for token `_id` is lower than the `_value` sent.
        MUST revert on any other error.
        MUST emit the `TransferSingle` event to reflect the balance change (see "Safe Transfer Rules" section of the standard).
        After the above conditions are met, this function MUST check if `_to` is a smart contract (e.g. code size > 0). If so, it MUST call `onERC1155Received` on `_to` and act appropriately (see "Safe Transfer Rules" section of the standard).
        @param _from    Source address
        @param _to      Target address
        @param _id      ID of the token type
        @param _value   Transfer amount
        @param _data    Additional data with no specified format, MUST be sent unaltered in call to `onERC1155Received` on `_to`
    */
    function safeTransferFrom(address _from, address _to, uint256 _id, uint256 _value, bytes calldata _data) external override {

        require(_to != address(0x0), "_to must be non-zero.");
        require(_from == msg.sender || operatorApproval[_from][msg.sender] == true, "Need operator approval for 3rd party transfers.");

        // SafeMath will throw with insuficient funds _from
        // or if _id is not valid (balance will be 0)
        balances[_id][_from] = balances[_id][_from].sub(_value);
        balances[_id][_to]   = _value.add(balances[_id][_to]);

        // MUST emit event
        emit TransferSingle(msg.sender, _from, _to, _id, _value);

        // Now that the balance is updated and the event was emitted,
        // call onERC1155Received if the destination is a contract.
        if (_to.isContract()) {
            _doSafeTransferAcceptanceCheck(msg.sender, _from, _to, _id, _value, _data);
        }
    }

    /**
        @notice Transfers `_values` amount(s) of `_ids` from the `_from` address to the `_to` address specified (with safety call).
        @dev Caller must be approved to manage the tokens being transferred out of the `_from` account (see "Approval" section of the standard).
        MUST revert if `_to` is the zero address.
        MUST revert if length of `_ids` is not the same as length of `_values`.
        MUST revert if any of the balance(s) of the holder(s) for token(s) in `_ids` is lower than the respective amount(s) in `_values` sent to the recipient.
        MUST revert on any other error.
        MUST emit `TransferSingle` or `TransferBatch` event(s) such that all the balance changes are reflected (see "Safe Transfer Rules" section of the standard).
        Balance changes and events MUST follow the ordering of the arrays (_ids[0]/_values[0] before _ids[1]/_values[1], etc).
        After the above conditions for the transfer(s) in the batch are met, this function MUST check if `_to` is a smart contract (e.g. code size > 0). If so, it MUST call the relevant `ERC1155TokenReceiver` hook(s) on `_to` and act appropriately (see "Safe Transfer Rules" section of the standard).
        @param _from    Source address
        @param _to      Target address
        @param _ids     IDs of each token type (order and length must match _values array)
        @param _values  Transfer amounts per token type (order and length must match _ids array)
        @param _data    Additional data with no specified format, MUST be sent unaltered in call to the `ERC1155TokenReceiver` hook(s) on `_to`
    */
    function safeBatchTransferFrom(address _from, address _to, uint256[] calldata _ids, uint256[] calldata _values, bytes calldata _data) external override {

        // MUST Throw on errors
        require(_to != address(0x0), "destination address must be non-zero.");
        require(_ids.length == _values.length, "_ids and _values array length must match.");
        require(_from == msg.sender || operatorApproval[_from][msg.sender] == true, "Need operator approval for 3rd party transfers.");

        for (uint256 i = 0; i < _ids.length; ++i) {
            uint256 id = _ids[i];
            uint256 value = _values[i];

            // SafeMath will throw with insuficient funds _from
            // or if _id is not valid (balance will be 0)
            balances[id][_from] = balances[id][_from].sub(value);
            balances[id][_to]   = value.add(balances[id][_to]);
        }

        // Note: instead of the below batch versions of event and acceptance check you MAY have emitted a TransferSingle
        // event and a subsequent call to _doSafeTransferAcceptanceCheck in above loop for each balance change instead.
        // Or emitted a TransferSingle event for each in the loop and then the single _doSafeBatchTransferAcceptanceCheck below.
        // However it is implemented the balance changes and events MUST match when a check (i.e. calling an external contract) is done.

        // MUST emit event
        emit TransferBatch(msg.sender, _from, _to, _ids, _values);

        // Now that the balances are updated and the events are emitted,
        // call onERC1155BatchReceived if the destination is a contract.
        if (_to.isContract()) {
            _doSafeBatchTransferAcceptanceCheck(msg.sender, _from, _to, _ids, _values, _data);
        }
    }

    /**
        @notice Get the balance of an account's Tokens.
        @param _owner  The address of the token holder
        @param _id     ID of the Token
        @return        The _owner's balance of the Token type requested
     */
    function balanceOf(address _owner, uint256 _id) external view override returns (uint256) {
        // The balance of any account can be calculated from the Transfer events history.
        // However, since we need to keep the balances to validate transfer request,
        // there is no extra cost to also provide a query function.
        return balances[_id][_owner];
    }


    /**
        @notice Get the balance of multiple account/token pairs
        @param _owners The addresses of the token holders
        @param _ids    ID of the Tokens
        @return        The _owner's balance of the Token types requested (i.e. balance for each (owner, id) pair)
     */
    function balanceOfBatch(address[] calldata _owners, uint256[] calldata _ids) external view override returns (uint256[] memory) {

        require(_owners.length == _ids.length);

        uint256[] memory balances_ = new uint256[](_owners.length);

        for (uint256 i = 0; i < _owners.length; ++i) {
            balances_[i] = balances[_ids[i]][_owners[i]];
        }

        return balances_;
    }

    /**
        @notice Enable or disable approval for a third party ("operator") to manage all of the caller's tokens.
        @dev MUST emit the ApprovalForAll event on success.
        @param _operator  Address to add to the set of authorized operators
        @param _approved  True if the operator is approved, false to revoke approval
    */
    function setApprovalForAll(address _operator, bool _approved) external override {
        operatorApproval[msg.sender][_operator] = _approved;
        emit ApprovalForAll(msg.sender, _operator, _approved);
    }

    /**
        @notice Queries the approval status of an operator for a given owner.
        @param _owner     The owner of the Tokens
        @param _operator  Address of authorized operator
        @return           True if the operator is approved, false if not
    */
    function isApprovedForAll(address _owner, address _operator) external view override returns (bool) {
        return operatorApproval[_owner][_operator];
    }

/////////////////////////////////////////// Internal //////////////////////////////////////////////

    function _doSafeTransferAcceptanceCheck(address _operator, address _from, address _to, uint256 _id, uint256 _value, bytes memory _data) internal {

        // If this was a hybrid standards solution you would have to check ERC165(_to).supportsInterface(0x4e2312e0) here but as this is a pure implementation of an ERC-1155 token set as recommended by
        // the standard, it is not necessary. The below should revert in all failure cases i.e. _to isn't a receiver, or it is and either returns an unknown value or it reverts in the call to indicate non-acceptance.


        // Note: if the below reverts in the onERC1155Received function of the _to address you will have an undefined revert reason returned rather than the one in the require test.
        // If you want predictable revert reasons consider using low level _to.call() style instead so the revert does not bubble up and you can revert yourself on the ERC1155_ACCEPTED test.
        require(ERC1155TokenReceiver(_to).onERC1155Received(_operator, _from, _id, _value, _data) == ERC1155_ACCEPTED, "contract returned an unknown value from onERC1155Received");
    }

    function _doSafeBatchTransferAcceptanceCheck(address _operator, address _from, address _to, uint256[] memory _ids, uint256[] memory _values, bytes memory _data) internal {

        // If this was a hybrid standards solution you would have to check ERC165(_to).supportsInterface(0x4e2312e0) here but as this is a pure implementation of an ERC-1155 token set as recommended by
        // the standard, it is not necessary. The below should revert in all failure cases i.e. _to isn't a receiver, or it is and either returns an unknown value or it reverts in the call to indicate non-acceptance.

        // Note: if the below reverts in the onERC1155BatchReceived function of the _to address you will have an undefined revert reason returned rather than the one in the require test.
        // If you want predictable revert reasons consider using low level _to.call() style instead so the revert does not bubble up and you can revert yourself on the ERC1155_BATCH_ACCEPTED test.
        require(ERC1155TokenReceiver(_to).onERC1155BatchReceived(_operator, _from, _ids, _values, _data) == ERC1155_BATCH_ACCEPTED, "contract returned an unknown value from onERC1155BatchReceived");
    }
}

/**
    @dev Mintable form of ERC1155
    Shows how easy it is to mint new items.
*/
contract ERC1155Mintable is ERC1155 {

    bytes4 constant private INTERFACE_SIGNATURE_URI = 0x0e89341c;

    // id => creators
    mapping (uint256 => address) public creators;

    function supportsInterface(bytes4 _interfaceId)
    public
    view
    override
    returns (bool) {
        if (_interfaceId == INTERFACE_SIGNATURE_URI) {
            return true;
        } else {
            return super.supportsInterface(_interfaceId);
        }
    }

    // Creates a new token type and assings _initialSupply to minter
    function _create(uint256 _id, uint256 _initialSupply) internal {
        creators[_id] = msg.sender;
        balances[_id][msg.sender] = _initialSupply;

        // Transfer event with mint semantic
        emit TransferSingle(msg.sender, address(0x0), msg.sender, _id, _initialSupply);
    }

    // Batch mint tokens. Assign directly to _to[].
    function _mint(uint256 _id, address[] memory _to, uint256[] memory _quantities) internal {

        for (uint256 i = 0; i < _to.length; ++i) {

            address to = _to[i];
            uint256 quantity = _quantities[i];

            // Grant the items to the caller
            balances[_id][to] = quantity.add(balances[_id][to]);

            // Emit the Transfer/Mint event.
            // the 0x0 source address implies a mint
            // It will also provide the circulating supply info.
            emit TransferSingle(msg.sender, address(0x0), to, _id, quantity);

            if (to.isContract()) {
                _doSafeTransferAcceptanceCheck(msg.sender, msg.sender, to, _id, quantity, '');
            }
        }
    }

    function _burn(uint256 _id, address[] memory _from, uint256[] memory _quantities) internal {

        for (uint256 i = 0; i < _from.length; ++i) {

            address from = _from[i];
            uint256 quantity = _quantities[i];

            // Will throw if intent to burn more than balance
            balances[_id][from] = balances[_id][from].sub(quantity);

            // Emit Transfer/Mint event
            // the 0x0 destination address implies a burn
            // It will also provide the circulating supply info.
            emit TransferSingle(msg.sender, from, address(0x0), _id, quantity);
        }
    }
}

contracts/ERC20.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity >=0.6.0 <0.9.0;

import "./interfaces/IERC20.sol";
import "./libraries/SafeMath.sol";

contract ERC20 is IERC20 {
	using SafeMath for uint256;
	event Transfer(address indexed from, address indexed to, uint256 value);
	event Approval(
		address indexed owner,
		address indexed spender,
		uint256 value
	);

	uint256 public override totalSupply;
	mapping(address => uint256) public override balanceOf;
	mapping(address => mapping(address => uint256)) public override allowance;
	string public override name;
	string public override symbol;
	uint8 public override decimals;

	constructor(
		string memory _name,
		string memory _symbol,
		uint8 _decimals
	) public {
		name = _name;
		symbol = _symbol;
		decimals = _decimals;
	}

	function transfer(
		address recipient,
		uint256 amount
	) external override returns (bool) {
		balanceOf[msg.sender] = balanceOf[msg.sender].sub(amount);
		balanceOf[recipient] = balanceOf[recipient].add(amount);
		emit Transfer(msg.sender, recipient, amount);
		return true;
	}

	function approve(
		address spender,
		uint256 amount
	) external override returns (bool) {
		allowance[msg.sender][spender] = amount;
		emit Approval(msg.sender, spender, amount);
		return true;
	}

	function transferFrom(
		address sender,
		address recipient,
		uint256 amount
	) external override returns (bool) {
		allowance[sender][msg.sender] = allowance[sender][msg.sender].sub(
			amount
		);
		balanceOf[sender] = balanceOf[sender].sub(amount);
		balanceOf[recipient] = balanceOf[recipient].add(amount);
		emit Transfer(sender, recipient, amount);
		return true;
	}

	function _mint(address to, uint256 amount) internal {
		balanceOf[to] = balanceOf[to].add(amount);
		totalSupply = totalSupply.add(amount);
		emit Transfer(address(0), to, amount);
	}

	function _burn(address from, uint256 amount) internal {
		balanceOf[from] = balanceOf[from].sub(amount);
		totalSupply = totalSupply.sub(amount);
		emit Transfer(from, address(0), amount);
	}

	function mint(address to, uint256 amount) external {
		_mint(to, amount);
	}

	function burn(address from, uint256 amount) external {
		_burn(from, amount);
	}
}

