// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity >=0.5.0;

/// @title BitMath
/// @dev This library provides functionality for computing bit properties of an unsigned integer
library BitMath {
	/// @notice Returns the index of the most significant bit of the number,
	///     where the least significant bit is at index 0 and the most significant bit is at index 255
	/// @dev The function satisfies the property:
	///     x >= 2**mostSignificantBit(x) and x < 2**(mostSignificantBit(x)+1)
	/// @param x the value for which to compute the most significant bit, must be greater than 0
	/// @return r the index of the most significant bit
	function mostSignificantBit(uint256 x) internal pure returns (uint8 r) {
		require(x > 0);

		if (x >= 0x100000000000000000000000000000000) {
			x >>= 128;
			r += 128;
		}
		if (x >= 0x10000000000000000) {
			x >>= 64;
			r += 64;
		}
		if (x >= 0x100000000) {
			x >>= 32;
			r += 32;
		}
		if (x >= 0x10000) {
			x >>= 16;
			r += 16;
		}
		if (x >= 0x100) {
			x >>= 8;
			r += 8;
		}
		if (x >= 0x10) {
			x >>= 4;
			r += 4;
		}
		if (x >= 0x4) {
			x >>= 2;
			r += 2;
		}
		if (x >= 0x2) r += 1;
	}

	/// @notice Returns the index of the least significant bit of the number,
	///     where the least significant bit is at index 0 and the most significant bit is at index 255
	/// @dev The function satisfies the property:
	///     (x & 2**leastSignificantBit(x)) != 0 and (x & (2**(leastSignificantBit(x)) - 1)) == 0)
	/// @param x the value for which to compute the least significant bit, must be greater than 0
	/// @return r the index of the least significant bit
	function leastSignificantBit(uint256 x) internal pure returns (uint8 r) {
		require(x > 0);

		r = 255;
		if (x & uint256(-1) > 0) {
			r -= 128;
		} else {
			x >>= 128;
		}
		if (x & uint64(-1) > 0) {
			r -= 64;
		} else {
			x >>= 64;
		}
		if (x & uint32(-1) > 0) {
			r -= 32;
		} else {
			x >>= 32;
		}
		if (x & uint16(-1) > 0) {
			r -= 16;
		} else {
			x >>= 16;
		}
		if (x & uint8(-1) > 0) {
			r -= 8;
		} else {
			x >>= 8;
		}
		if (x & 0xf > 0) {
			r -= 4;
		} else {
			x >>= 4;
		}
		if (x & 0x3 > 0) {
			r -= 2;
		} else {
			x >>= 2;
		}
		if (x & 0x1 > 0) r -= 1;
	}
}

contracts/libraries/ButterLibrary.sol.content:
pragma solidity >=0.5.0;

import "../interfaces/IButterPair.sol";

import "./SafeMath.sol";

library ButterLibrary {
	using SafeMath for uint;

	// returns sorted token addresses, used to handle return values from pairs sorted in this order
	function sortTokens(
		address tokenA,
		address tokenB
	) internal pure returns (address token0, address token1) {
		require(tokenA != tokenB, "ButterLibrary: IDENTICAL_ADDRESSES");
		(token0, token1) = tokenA < tokenB
			? (tokenA, tokenB)
			: (tokenB, tokenA);
		require(token0 != address(0), "ButterLibrary: ZERO_ADDRESS");
	}

	// calculates the CREATE2 address for a pair without making any external calls
	function pairFor(
		address factory,
		address tokenA,
		address tokenB
	) internal pure returns (address pair) {
		(address token0, address token1) = sortTokens(tokenA, tokenB);
		pair = address(
			uint(
				keccak256(
					abi.encodePacked(
						hex"ff",
						factory,
						keccak256(abi.encodePacked(token0, token1)),
						hex"bd453c3b70eca359ba7be49a87c127fbd47c6ccf8208bb3159d50081c3d0aae4" // init code hash
					)
				)
			)
		);
	}

	// fetches and sorts the reserves for a pair
	function getReserves(
		address factory,
		address tokenA,
		address tokenB
	) internal view returns (uint reserveA, uint reserveB) {
		(address token0, ) = sortTokens(tokenA, tokenB);
		(uint reserve0, uint reserve1, , , ) = IButterPair(
			pairFor(factory, tokenA, tokenB)
		).getReserves();
		(reserveA, reserveB) = tokenA == token0
			? (reserve0, reserve1)
			: (reserve1, reserve0);
	}

	// given some amount of an asset and pair reserves, returns an equivalent amount of the other asset
	function quote(
		uint amountA,
		uint reserveA,
		uint reserveB
	) internal pure returns (uint amountB) {
		require(amountA > 0, "ButterLibrary: INSUFFICIENT_AMOUNT");
		require(
			reserveA > 0 && reserveB > 0,
			"ButterLibrary: INSUFFICIENT_LIQUIDITY"
		);
		amountB = amountA.mul(reserveB) / reserveA;
	}

	// given an input amount of an asset and pair reserves, returns the maximum output amount of the other asset
	function getAmountOut(
		uint amountIn,
		uint reserveIn,
		uint reserveOut
	) internal pure returns (uint amountOut) {
		require(amountIn > 0, "ButterLibrary: INSUFFICIENT_INPUT_AMOUNT");
		require(
			reserveIn > 0 && reserveOut > 0,
			"ButterLibrary: INSUFFICIENT_LIQUIDITY"
		);
		uint amountInWithFee = amountIn.mul(997);
		uint numerator = amountInWithFee.mul(reserveOut);
		uint denominator = reserveIn.mul(1000).add(amountInWithFee);
		amountOut = numerator / denominator;
	}

	// given an output amount of an asset and pair reserves, returns a required input amount of the other asset
	function getAmountIn(
		uint amountOut,
		uint reserveIn,
		uint reserveOut
	) internal pure returns (uint amountIn) {
		require(amountOut > 0, "ButterLibrary: INSUFFICIENT_OUTPUT_AMOUNT");
		require(
			reserveIn > 0 && reserveOut > 0,
			"ButterLibrary: INSUFFICIENT_LIQUIDITY"
		);
		uint numerator = reserveIn.mul(amountOut).mul(1000);
		uint denominator = reserveOut.sub(amountOut).mul(997);
		amountIn = (numerator / denominator).add(1);
	}

	// performs chained getAmountOut calculations on any number of pairs
	function getAmountsOut(
		address factory,
		uint amountIn,
		address[] memory path
	) internal view returns (uint[] memory amounts) {
		require(path.length >= 2, "ButterLibrary: INVALID_PATH");
		amounts = new uint[](path.length);
		amounts[0] = amountIn;
		for (uint i; i < path.length - 1; i++) {
			(uint reserveIn, uint reserveOut) = getReserves(
				factory,
				path[i],
				path[i + 1]
			);
			amounts[i + 1] = getAmountOut(amounts[i], reserveIn, reserveOut);
		}
	}

	// performs chained getAmountIn calculations on any number of pairs
	function getAmountsIn(
		address factory,
		uint amountOut,
		address[] memory path
	) internal view returns (uint[] memory amounts) {
		require(path.length >= 2, "ButterLibrary: INVALID_PATH");
		amounts = new uint[](path.length);
		amounts[amounts.length - 1] = amountOut;
		for (uint i = path.length - 1; i > 0; i--) {
			(uint reserveIn, uint reserveOut) = getReserves(
				factory,
				path[i - 1],
				path[i]
			);
			amounts[i - 1] = getAmountIn(amounts[i], reserveIn, reserveOut);
		}
	}
}

